mapper.select:
SELECT log_id as logId, l.class_id as classId, content as content, l.crt_time as crtTime, class_time as classTime, class_nm as classNm FROM t_class_log l, t_class t WHERE t.class_id = l.class_id
AND t.class_id = #{classId,jdbcType=BIGINT}
AND l.log_id = #{logId,jdbcType=BIGINT}
order by crtTime desc
limit #{start, jdbcType=INTEGER}, #{end, jdbcType=INTEGER};

SELECT a.detail_log_id AS detailLogId, b.log_id AS logId, b.class_time as classTime FROM t_class_log_detail a, t_class_log b where a.log_id = b.log_id and
log_obj_id = #{logObjId,jdbcType=BIGINT}
AND( log_type = 2 or log_type = 3)
and log_type = #{logType,jdbcType=TINYINT}
ORDER BY b.class_time desc
limit #{start, jdbcType=INTEGER}, #{end, jdbcType=INTEGER};
